# first column - targets (digits 1-9)

import matplotlib.pyplot as plt
import numpy as np


def load_data(file_path):
    with open(file_path, "r") as f:
        data = f.readlines()
    return data


def visualize_data(data):
    targets = []
    for line in data:
        target = line.split(",")[0]
        targets.append(target)
    targets = np.array(targets)
    unique, counts = np.unique(targets, return_counts=True)
    plt.bar(unique, counts)
    plt.show()


def filter_data(data, target):
    filtered_data = []
    for line in data:
        if line.split(",")[0] == target:
            filtered_data.append(line)
    return filtered_data


# visualize average for each pixel in form of picture
def average_image(data):
    image = np.zeros((28, 28))
    for line in data:
        pixels = line.split(",")[1:]
        pixels = np.array(pixels, dtype=int)
        pixels = pixels.reshape(28, 28)
        image += pixels
    image = image / len(data)
    plt.imshow(image, cmap="gray")
    plt.show()


def main():
    file_path = "data.csv"
    data = load_data(file_path)
    visualize_data(data)
    filtered_data = filter_data(data, "1")
    average_image(filtered_data)


if __name__ == "__main__":
    main()
